// import React from "react";

function Home() {
    return (
      <div style={styles.container}>
        <div style={styles.overlay}>
          <div style={styles.content}>
            <h2>Welcome to DocStamp!</h2>
            <p>Easily upload, manage, and stamp your documents with security.</p>
            <button style={styles.button}>Get Started</button>
          </div>
        </div>
      </div>
    );
  }
  
  const styles = {
    container: {
      height: "100vh", // Full screen height
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      backgroundImage: "url('https://via.placeholder.com/1200x800')", // Your image URL
      backgroundSize: "cover", // Ensure it covers the full page
      backgroundPosition: "center",
    },
    overlay: {
      position: "absolute",
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      backgroundColor: "rgba(0, 0, 0, 0.5)", // Dark overlay for contrast
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
    },
    content: {
      textAlign: "center",
      color: "white", // White text for visibility
      fontFamily: "Arial, sans-serif",
      padding: "20px",
    },
    button: {
      padding: "0.7rem 1.5rem",
      backgroundColor: "#007BFF",
      color: "#FFF",
      border: "none",
      borderRadius: "4px",
      cursor: "pointer",
      fontSize: "16px",
      transition: "background-color 0.3s ease",
    },
    buttonHover: {
      backgroundColor: "#0056b3",
    },
  };
  
  export default Home;
